/*base values*/
:root {

	--letter_spacing: 0px;

	@property --correct_insertion_color {
		syntax: "<color>";
		inherits: true;
		initial-value: hsl(106, 100%, 25%);
	}

	@property --incorrect_omission_color {
		syntax: "<color>";
		inherits: true;
		initial-value: hsl(211, 100%, 40%);
	}

	@property --incorrect_omission_color {
		syntax: "<color>";
		inherits: true;
		initial-value: hsl(3, 100%, 30%);
	}

	@property --text_shadow_color {
		syntax: "<color>";
		inherits: true;
		initial-value: grey;
	}

	@property --selection_background_color {
		syntax: "<color>";
		inherits: true;
		initial-value: hsl(52, 100%, 44%);
	}

	@property --code_color {
		syntax: "<color>";
		inherits: true;
		initial-value: #850a47;
	}

	@property --safe_color {
		syntax: "<color>";
		inherits: true;
		initial-value: green;
	}

	@property --link_color {
		syntax: "<color>";
		inherits: true;
		initial-value: blue;
	}

	@property --textfield_background_color {
		syntax: "<color>";
		inherits: true;
		initial-value: inherit;
	}

	@property --textfield_color {
		syntax: "<color>";
		inherits: true;
		initial-value: black;
	}

	@property --hint_color {
		syntax: "<color>";
		inherits: true;
		initial-value: brown;
	}

	@property --hr_border_color {
		syntax: "<color>";
		inherits: true;
		initial-value: black;
	}

	@property --hr_opacity {
		syntax: "<number>";
		inherits: true;
		initial-value: 1;
	}

	@property --border_width_thin {
		syntax: "<length>";
		inherits: true;
		initial-value: 0.01rem;
	}

	@property --border_width_mid {
		syntax: "<length>";
		inherits: true;
		initial-value: 0.1rem;
	}

	@property --border_width_thick {
		syntax: "<length>";
		inherits: true;
		initial-value: 0.5rem;
	}

	@property --border_radius_large {
		syntax: "<length>";
		inherits: true;
		initial-value: 0.5rem;
