/*base values*/

:root {

	--letter_spacing: 0px;

	@property --correct_insertion_color {
		syntax: "<color>";
		inherits: true;
		initial-value: hsl(106, 100%, 25%);
	}

	@property --incorrect_omission_color {
		syntax: "<color>";
		inherits: true;
		initial-value: hsl(211, 100%, 40%);
	}

	@property --incorrect_omission_color {
		syntax: "<color>";
		inherits: true;
		initial-value: hsl(3, 100%, 30%);
	}

	@property --text_shadow_color {
		syntax: "<color>";
		inherits: true;
		initial-value: grey;
	}

	@property --selection_background_color {
		syntax: "<color>";
		inherits: true;
		initial-value: hsl(52, 100%, 44%);
	}

	@property --code_color {
		syntax: "<color>";
		inherits: true;
		initial-value: #850a47;
	}

	@property --safe_color {
		syntax: "<color>";
		inherits: true;
		initial-value: green;
	}

	@property --link_color {
		syntax: "<color>";
		inherits: true;
		initial-value: blue;
	}

	@property --textfield_background_color {
		syntax: "<color>";
		inherits: true;
		initial-value: inherit;
	}

	@property --textfield_color {
		syntax: "<color>";
		inherits: true;
		initial-value: black;
	}

	@property --hint_color {
		syntax: "<color>";
		inherits: true;
		initial-value: brown;
	}

	@property --hr_border_color {
		syntax: "<color>";
		inherits: true;
		initial-value: black;
	}

	@property --hr_opacity {
		syntax: "<number>";
		inherits: true;
		initial-value: 1;
	}

	@property --border_width_thin {
		syntax: "<length>";
		inherits: true;
		initial-value: 0.01rem;
	}

	@property --border_width_mid {
		syntax: "<length>";
		inherits: true;
		initial-value: 0.1rem;
	}

	@property --border_width_thick {
		syntax: "<length>";
		inherits: true;
		initial-value: 0.5rem;
	}

	@property --border_radius_large {
		syntax: "<length>";
		inherits: true;
		initial-value: 0.5rem;
	}

	@property --summary_height {
		syntax: "<length>";
		inherits: true;
		initial-value: 2.5rem;
	}

	@property --font_color {
		syntax: "<color>";
		inherits: true;
		initial-value: 2.5rem;
	}

	@property --background_color {
		syntax: "<color>";
		inherits: true;
		initial-value: 2.5rem;
	}

	--font_color: black;
	--background_color: white;
	--correct_insertion_color: hsl(120, 75%, 75%);
	--incorrect_omission_color: hsl(211, 75%, 65%);
	--incorrect_insertion_color: hsl(3, 75%, 64%);
	--text_shadow_color: grey;
	--selection_background_color: hsl(52, 100%, 46%);
	--code_color: #850a47;
	--safe_color: green;
	--link_color: blue;
	--textfield_color: black;
	--textfield_background_color: inherit;  
	--hint_color: brown;
	--hr_border-color: black;
	--hr_opacity: 1;

	--border_width_thin: 0.01rem;
	--border_width_mid: 0.1rem;
	--border_width_thick: 0.5rem;
	--border_radius_large: 0.5rem;
	--summary_height: 2.5rem;



	/*my old value*/
	/*--font-size-code: 1.3rem;*/
	--font_size_code: 1em;

	/*old*/
	--line_height_code:  1.1;
	/*addon: --line_height_code:  1.5 !important;*/

}

:root.night-mode, :root[data-bs-theme="dark"], :root[data-darkreader-scheme="dark"]{
	--correct_insertion_color: hsl(106, 100%, 25%);
	--incorrect_omission_color: hsl(211, 100%, 40%);
	--incorrect_insertion_color: hsl(3, 100%, 30%);
	--text_shadow_color: black;
	--selection_background_color: hsl(52, 100%, 38%);
	--code_color: lightgreen;
	--safe_color: lightgreen;
	--link_color: #00ccff;
	--textfield_color: white;
	--textfield_background_color: #0d1117;
	--hint_color: orange;
	--hr_border-color: lightgray;
	--hr_opacity: 0.5;
}

/*color scheme*/

@media (prefers-color-scheme: dark){
	:root{
		--correct_insertion_color: hsl(106, 100%, 25%);
		--incorrect_omission_color: hsl(211, 100%, 40%);
		--incorrect_insertion_color: hsl(3, 100%, 30%);
		--text_shadow_color: black;
		--selection_background_color: hsl(52, 100%, 38%);
		--code_color: lightgreen;
		--safe_color: lightgreen;
		--link_color: #00ccff;
		--textfield_color: white;
		--textfield_background_color: #0d1117;
		--hint_color: orange;
		--hr_border-color: lightgray;
		--hr_opacity: 0.5;

		--font_color: white;
		--background_color: black;
	}

	html {
		background: var(--background_color) !important;
	}

	.count {
		&.new {
			color: rgb(51, 125, 255);;
		}

		&.learn {
			color: rgb(255, 97, 97);
		}

		&.review {
			color: rgb(97, 255, 97);
		}
	}

	nav.navbar .navbar-nav .nav-item a.nav-link {
		color: lightgray;

		&:hover {
			color: grey;
		}
	}

	body {
		/*overriden later, potentially remove*/
		color: lightgray;


		& .navbar-brand {
			color: #e8e6e3;

			&:hover {
				color: grey;
			}
		}
	}

	body{
		color: var(--font_color);
		background-color: var(--background_color);
	}

	div#ansarea.svelte-192a69y{
		background-color: transparent !important;
	}



	nav, #ansarea-pad, #ansarea {
		/*background-color: hsla(215, 100%, 33%, 0.5) !important;*/
		background-color: transparent !important;	
	}

	button {
		color: white;
		border-color: white;
	}
}

/*increase contrast*/

@media (prefers-contrast: less){
	.card{
		font-weight: 200;
	}
}

@media (prefers-contrast: more) {
	.card{
		font-weight: 700;
	}

	.correct, .deleted, .inserted {
		text-shadow: 1px 1px var(--text_shadow_color);
	}

	.back #field1 {
		text-shadow: 1px 1px var(--text_shadow_color);
		text-align: left;
	}
  }

/*reduce motion*/

@media (prefers-reduced-motion: no-preference) {
	:root {
		--animation_duration: 0.7s;
		--font_size_start: 0%;
		--font_size_end: var(--font_size_code);
	}
}

/*Needs to be improved!!*/
@media (prefers-reduced-motion: reduce) {
	:root {
		--animation_duration: 3s;
		--font_size_start: 0%;
		--font_size_end: var(--font_size_code);
	}
}

