.token.tab:not(:empty),
.token.cr,
.token.lf,
.token.space {
	position: relative;
}

.token.tab:not(:empty)::before,
.token.cr::before,
.token.lf::before,
.token.space::before {
	color: #808080;
	opacity: 0.6;
	position: absolute;
}

.token.tab:not(:empty)::before {
	content: '\21E5';
}

.token.cr::before {
	content: '\240D';
}

.token.crlf::before {
	content: '\240D\240A';
}
.token.lf::before {
	content: '\240A';
}

.token.space::before {
	content: '\00B7';
}

.token.punctuation.brace-hover,
.token.punctuation.brace-selected {
	outline: solid 1px;
}

.rainbow-braces .token.punctuation.brace-level-1,
.rainbow-braces .token.punctuation.brace-level-5,
.rainbow-braces .token.punctuation.brace-level-9 {
	color: #E50;
	opacity: 1;
}
