a {
	text-decoration-line: none;
	color: var(--link_color);
}

details {
	font-size: 80%;
	text-align: left;
	margin: auto;

	& > summary {
		margin-top: 3rem;
		padding: 0.5em;
		cursor: pointer;
		border: 1px solid grey;
		border-radius: 0.5rem;
		width: 5em;
	}

	& > div {
		font-size: 90%;
