}

.card {
	font-family: arial; 
	font-size: 1.5rem;
	color: black;
	background-color: white;
	text-align: center;
	max-width: 1024px;
	margin: auto;
	padding-top: 0.01rem;
	padding-bottom: 0.01rem;;
	padding-left: 0.5rem;
	padding-right: 0.5rem;	
	scrollbar-width: none;
}

.deck {
	font-size: 65%;
}

.hint {
	font-size: 85%;
	color: var(--hint_color);
}

.subdeck {
		font-size: 65%;
}

.value, .description, .bezeichnung {
		display: inline-block;
		text-align: left;
		padding: o.5rem;
}

.bezeichnung {
		font-size: 150%;
}

.beispiele, .unterformen {
		font-size: 80%;
}

.helper {
	text-transform: uppercase;
	padding-top: 0.4rem;
	font-size: 60%;
}

::-webkit-scrollbar {
	display: none;
}

.logo {
	width: 2rem;
	height: auto;
}

div {
	padding-bottom: 0.5rem !important;
}

.subdeck {
	font-size: 70%;
	width: 50vw;
	margin: auto;
}

.helper {
	padding: 0.5 rem;
	margin: 0.5 rem;
}

img {
	height: 10px;
}

.logo {
	height: 1em;
	width: auto;
	padding: 0 0 0 0.5em;
	border: 0;
	margin: 0;
	position: absolute;
	top: 50%;
	transform: translate(0, -50%);
}

.description {
	text-align: left;
	padding-top: 1rem;
}

body .btn {
	border-width: 0.1rem;
}

:root {

	--letter_spacing: 0px;

	@property --correct_insertion_color {
		syntax: "<color>";
		inherits: true;
		initial-value: hsl(106, 100%, 25%);
	}

	@property --incorrect_omission_color {
		syntax: "<color>";
		inherits: true;
		initial-value: hsl(211, 100%, 40%);
	}

	@property --incorrect_omission_color {
		syntax: "<color>";
		inherits: true;
		initial-value: hsl(3, 100%, 30%);
	}

	@property --text_shadow_color {
		syntax: "<color>";
		inherits: true;
		initial-value: grey;
	}

	@property --selection_background_color {
		syntax: "<color>";
		inherits: true;
		initial-value: hsl(52, 100%, 44%);
	}

	@property --code_color {
		syntax: "<color>";
		inherits: true;
		initial-value: #850a47;
	}

	@property --safe_color {
		syntax: "<color>";
		inherits: true;
		initial-value: green;
	}

	@property --link_color {
		syntax: "<color>";
		inherits: true;
		initial-value: blue;
	}

	@property --textfield_background_color {
		syntax: "<color>";
		inherits: true;
		initial-value: inherit;
	}

	@property --textfield_color {
		syntax: "<color>";
		inherits: true;
		initial-value: black;
	}

	@property --hint_color {
		syntax: "<color>";
		inherits: true;
		initial-value: brown;
	}

	@property --hr_border_color {
		syntax: "<color>";
		inherits: true;
		initial-value: black;
	}

	@property --hr_opacity {
		syntax: "<color>";
		inherits: true;
		initial-value: 1;
	}


	--correct_insertion_color: hsl(120, 75%, 75%);
	--incorrect_omission_color: hsl(211, 75%, 65%);
	--incorrect_insertion_color: hsl(3, 75%, 64%);
	--text_shadow_color: grey;
	--selection_background_color: hsl(52, 100%, 46%);
	--code_color: #850a47;
	--safe_color: green;
	--link_color: blue;
	--textfield_color: black;
	--textfield_background_color: inherit; 
	/*rgb(250, 250, 250);*/ 
	--hint_color: brown;
	--hr_border-color: black;
	--hr_opacity: 1;
}

:root.night-mode, :root[data-darkreader-scheme="dark"]{
	--correct_insertion_color: hsl(106, 100%, 25%);
	--incorrect_omission_color: hsl(211, 100%, 40%);
	--incorrect_insertion_color: hsl(3, 100%, 30%);
	--text_shadow_color: black;
	--selection_background_color: hsl(52, 100%, 38%);
	--code_color: lightgreen;
 /*#d63384;*/
	--safe_color: lightgreen;
	--link_color: #00ccff;
	--textfield_color: white;
	--textfield_background_color: #0d1117;
	--hint_color: orange;
	--hr_border-color: lightgray;
	--hr_opacity: 0.5;
}

@media (prefers-color-scheme: dark){
	:root{
		--correct_insertion_color: hsl(106, 100%, 25%);
		--incorrect_omission_color: hsl(211, 100%, 40%);
		--incorrect_insertion_color: hsl(3, 100%, 30%);
		--text_shadow_color: black;
		--selection_background_color: hsl(52, 100%, 38%);
		--code_color: lightgreen;
 /*#d63384;*/
		--safe_color: lightgreen;
		--link_color: #00ccff;
		--textfield_color: white;
		--textfield_background_color: #0d1117;
		--hint_color: orange;
		--hr_border-color: lightgray;
		--hr_opacity: 0.5;
	}


	.count {
		&.new {
			color: rgb(51, 125, 255);;
		}

		&.learn {
			color: rgb(255, 97, 97);
		}

		&.review {
			color: rgb(97, 255, 97);
		}
	}

	nav.navbar .navbar-nav .nav-item a.nav-link {
		color: lightgray;

		&:hover {
			color: grey;
		}
	}

	body {
		color: lightgray;

		& .navbar-brand {
			color: #e8e6e3;

			&:hover {
				color: grey;
			}
		}
	}
}


@media (prefers-contrast: more) {
	.correct, .deleted, .inserted {
		text-shadow: 1px 1px var(--text_shadow_color);
	}

	.back #field1 {
		text-shadow: 1px 1px var(--text_shadow_color);
		text-align: left;
	}
  }

.back #field1 {
	& ::selection {
		background-color: var(--selection_background_color);
	}
}

@media (prefers-reduced-motion: no-preference) {
	:root {
		--animation_duration: 0.7s;
		--font_size_start: 0%;
		--font_size_end: 100%;
	}
}  

@media (prefers-reduced-motion: reduce) {
	:root {
		--animation_duration: 2s;
		--font_size_start: 100%;
		--font_size_end: 100%;
	}
}

@keyframes insertion_correct {
	from {background-color: transparent;}
	to {background-color: var(--correct_insertion_color);}
}

@keyframes omission_incorrect {
	from {
		background-color: transparent;
		font-size: var(--font_size_start);
	}
	to {
		background-color: var(--incorrect_omission_color);
		font-size: var(--font_size_end);
	}
}

@keyframes insertion_incorrect {
	from {background-color: transparent;}
	to {background-color: var(--incorrect_insertion_color)}
}

@media only screen and (max-width: 600px){
	details > div {
		column-count: 1;
	}	
}

@media only screen and (min-width: 601px) and (max-width: 1000px){
	details > div {
		column-count: 2;
	}	
}

@media only screen and (min-width: 1001px){
	details > div {
		column-count: 3;
	}	
}

