.grid_container {
	display: grid;
	grid-column-gap: 2vw;
	min-height: 80vh;
	/*con "height": text box switches size when keyboard appears or dissapears on mobile*/
}

.grid_item_1, .grid_item_2 {
	display: contents;
}

